C
C
      SUBROUTINE HSTLST
C
C
      COMMON /HSTGC1/ HORZNT, PERCNT, MIDVAL, SHADE, MEDIAN, PERIM,
     -       HFRAME, LISTOP, WINDOW, COLORS, HSTFOR, TITLE, LABEL,
     -       FREQNC, HWIND(4), COLSHA, COLREC, COLAXI, COLMED, COLTEX,
     -       COLTIT, COLPER
      LOGICAL HORZNT, PERCNT, MIDVAL, SHADE, MEDIAN, PERIM, HFRAME,
     -        LISTOP, WINDOW, COLORS, HSTFOR, TITLE, LABEL, FREQNC
      COMMON /HSTGC2/ STRFOR, STRTIT, STRLAB, STRFRE
      CHARACTER*55  STRFOR, STRTIT, STRLAB, STRFRE
C
      SAVE
C
C  GET THE STANDARD OUTPUT UNIT TO WRITE THE OPTION VALUE LIST
C
      IUNIT = I1MACH(2)
      WRITE(IUNIT,1000)
 1000 FORMAT(//' HISTOGRAM OPTION VALUE LIST'/)
C
C  HORIZONTAL
C
      WRITE(IUNIT,1001)
 1001 FORMAT(5X,'HORIZONTAL, HOR')
      IF (HORZNT) THEN
          WRITE(IUNIT,1111)
      ELSE
          WRITE(IUNIT,1110)
      ENDIF
C
C  PERCENT AXIS
C
      WRITE(IUNIT,1002)
 1002 FORMAT(5X,'PERCENT, PER')
      IF (PERCNT) THEN
          WRITE(IUNIT,1111)
      ELSE
          WRITE(IUNIT,1110)
      ENDIF
C
C  MIDVALUES LABELED
C
      WRITE(IUNIT,1003)
 1003 FORMAT(5X,'MIDVALUE, MID')
      IF (MIDVAL) THEN
          WRITE(IUNIT,1111)
      ELSE
          WRITE(IUNIT,1110)
      ENDIF
C
C  SHADING
C
      WRITE(IUNIT,1004)
 1004 FORMAT(5X,'SHADING, SHA')
      IF (SHADE) THEN
          WRITE(IUNIT,1111)
      ELSE
          WRITE(IUNIT,1110)
      ENDIF
C
C  MEDIAN
C
      WRITE(IUNIT,1005)
 1005 FORMAT(5X,'MEDIAN, MED')
      IF (MEDIAN) THEN
          WRITE(IUNIT,1111)
      ELSE
          WRITE(IUNIT,1110)
      ENDIF
C
C  PERIMETER
C
      WRITE(IUNIT,1006)
 1006 FORMAT(5X,'PERIMETER, PRM')
      IF (PERIM) THEN
          WRITE(IUNIT,1111)
      ELSE
          WRITE(IUNIT,1110)
      ENDIF
C
C  FRAME CALLED
C
      WRITE(IUNIT,1007)
 1007 FORMAT(5X,'FRAME ADVANCE, FRA')
      IF (HFRAME) THEN
          WRITE(IUNIT,1111)
      ELSE
          WRITE(IUNIT,1110)
      ENDIF
C
C  WINDOW SET
C
      WRITE(IUNIT,1008)
 1008 FORMAT(5X,'WINDOW (VIEWPORT), WIN')
      IF (WINDOW) THEN
          WRITE(IUNIT,1111)
          WRITE(IUNIT,1104)(HWIND(I),I=1,4)
 1104 FORMAT(24X,'XMIN  =',F6.2/
     -       24X,'XMAX  =',F6.2/
     -       24X,'YMIN  =',F6.2/
     -       24X,'YMAX  =',F6.2)
      ELSE
          WRITE(IUNIT,1110)
      ENDIF
C
C  COLORS SET
C
      WRITE(IUNIT,1009)
 1009 FORMAT(5X,'COLORS, COL')
      IF (COLORS) THEN
          WRITE(IUNIT,1111)
      ELSE
          WRITE(IUNIT,1110)
      ENDIF
          WRITE(IUNIT,1105)COLSHA,COLREC,COLAXI,COLMED,COLTEX,
     -    COLTIT,COLPER
 1105 FORMAT(19X,'SHADING    =',F4.0/
     -       19X,'RECTANGLES =',F4.0/
     -       19X,'AXES       =',F4.0/
     -       19X,'MEDIAN     =',F4.0/
     -       19X,'TEXT       =',F4.0/
     -       19X,'TITLE      =',F4.0/
     -       19X,'PERIMETER  =',F4.0)
C
C  CLASS INTERVAL FORMAT CHANGED
C
      WRITE(IUNIT,1010)
 1010 FORMAT(5X,'FORMAT, FOR')
      IF (HSTFOR) THEN
          WRITE(IUNIT,1111)
          WRITE(IUNIT,1106)STRFOR
 1106 FORMAT(8X,'CLASS INTERVAL FORMAT = ',55A)
      ELSE
          WRITE(IUNIT,1110)
      ENDIF
C
C  TITLE
C
      WRITE(IUNIT,1011)
 1011 FORMAT(5X,'TITLE, TIT')
      IF (TITLE) THEN
          WRITE(IUNIT,1111)
          WRITE(IUNIT,1107)STRTIT
 1107 FORMAT(20X,'TITLE : ',55A)
      ELSE
          WRITE(IUNIT,1110)
      ENDIF
C
C  CLASS INTERVAL LABEL CHANGED
C
      WRITE(IUNIT,1012)
 1012 FORMAT(5X,'CLASS INTERVAL LABEL CHANGED, LAB')
      IF (LABEL) THEN
          WRITE(IUNIT,1111)
          WRITE(IUNIT,1108)STRLAB
 1108 FORMAT(20X,'CLASS INTERVAL LABEL : ',55A)
      ELSE
          WRITE(IUNIT,1110)
      ENDIF
C
C  FREQUENCY LABEL CHANGED
C
      WRITE(IUNIT,1013)
 1013 FORMAT(5X,'FREQUENCY LABEL, FRE')
      IF (FREQNC) THEN
          WRITE(IUNIT,1111)
          WRITE(IUNIT,1109)STRFRE
 1109 FORMAT(20X,'FREQUENCY LABEL : ',55A)
      ELSE
          WRITE(IUNIT,1110)
      ENDIF
C
C
 1110 FORMAT(30X,'= OFF')
 1111 FORMAT(30X,'= ON')
C
      RETURN
      END
